import { blogs } from "../../data/Blogs";
import { Blog_Card } from "../Portal";

const Magazin = () => {
  return (
    <section className="container padding">
      <div className="box grid grid-cols-4">
        <div className="col-span-1 relative">
          <div className="card absolute w-[180%] left-[-30%]">
            <h3 className="title-lg text-justify">
              مجله‌ی امپــــراتوری از سراسر دنیای تکنولوژی مقاله توشه!
            </h3>
          </div>
        </div>
        <div className="col-span-3 grid grid-cols-2 gap-3 -translate-x-32">
          {blogs.slice(-4).map((blog, index) => {
            return (
              <Blog_Card
                blog={blog}
                key={index}
                customClass={index % 2 === 0 ? "-translate-y-20" : ""}
              />
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Magazin;
