import { teamMembers } from "../../data/Constants";

const About_Team = () => {
  return (
    <section className="padding text-center container">
      <h3 className="title-lg mb-10">
        <span className="halfbg-left">آینده ی شما در دستان مطمئنی است</span>
      </h3>
      <p>با تیم کار کشته ی امپراتوری کد آشنا شو :)</p>
      <div className="flex mt-12">
        {teamMembers.map((item, index) => {
          return (
            <div
              className={`border-4 border-blue-300 text-slate-50 group relative rounded-lg overflow-hidden${
                index == 1 ? " scale-105 z-10" : ""
              }`}
              key={index}
            >
              <div className="text-center absolute left-0 right-0 bottom-0 pb-5 min-h-10 rounded-t-3xl bg-slate-800 bg-opacity-50 transition-all group-hover:rounded-none group-hover:min-h-full">
                <h2 className="title">{item.name}</h2>
                <h4 className="tex-slate-200 font-thin text-slate-200 text-xs">
                  {item.role}
                </h4>
                <p className="mt-4 max-w-52 mx-auto">{item.bio}</p>
              </div>
              <img src={item.profile} alt={"پروفایل " + item.name} />
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default About_Team;
