import { initAsyncCompiler } from "sass";
import { learnersRank } from "../../data/Ranking";

const Learners_Rank = () => {
  const tableHeading = ["جایگاه", "پروفایل", "نام", "وضعیت", "رتبه", "دوره"];
  return (
    <section className="container text-center padding-y">
      <h3 className="title-lg">
        <span className="halfbg-right">برترین دانشجویان شهریور ماه</span>
      </h3>
      <table class="w-full mt-12 border border-slate-500">
        <thead className="bg-slate-900 text-slate-100">
          <tr>
            {tableHeading.map((item, index) => {
              return (
                <th className="py-5" key={index}>
                  {item}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody className="">
          {learnersRank.map((item, index) => {
            return (
              <tr className="">
                <td className="p-5">{index + 1}</td>
                <td className="py-5">
                  <img
                    src={item.profile + "?username=" + index}
                    alt={"پروفایل " + item.name}
                    className="size-16 mx-auto"
                  />
                </td>
                <td className="p-5">{item.name}</td>
                <td className="p-5">
                  {item.isLearning ? (
                    <span className="button-dark p-2">درحال یادگیری</span>
                  ) : (
                    <span className="button-blue p-2">پـایـان یادگیری</span>
                  )}
                </td>
                <td className="p-5">
                  {item.rank}
                  {item.rank == "جاویدان" && (
                    <i className="fa-duotone fa-solid fa-crown text-purple-400 me-1" />
                  )}
                </td>
                <td className="p-5">{item.course}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </section>
  );
};

export default Learners_Rank;
