import { blogs } from "../../data/Blogs";
import { Blog_Card } from "../Portal";

const Magazin = () => {
  return (
    <section className="container padding">
      <div className="box grid grid-cols-4">
        <div className="col-span-3 grid grid-cols-2 gap-3 -translate-x-32">
          {blogs.slice(-4).map((blog, index) => {
            return <Blog_Card blog={blog} key={index} />;
          })}
        </div>
        <div className="col-span-1">
          <div className="card">
            <h3 className="title-lg">
              مجله‌ی امپراتوری از سراسر دنیای تکنولوژی رو یادبگیر
            </h3>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Magazin;
