import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import { Pagination, Navigation } from "swiper/modules";
import { companies } from "../../data/Constants";

const Companies = () => {
  return (
    <section>
      <Swiper
        slidesPerView={5}
        centeredSlides={true}
        spaceBetween={100}
        loop={true}
        navigation={true}
        modules={[Pagination, Navigation]}
        className="companies logo"
      >
        {companies.map((item, index) => {
          return (
            <SwiperSlide key={index}>
              <a href={item.href}>
                <img
                  src={item.logo}
                  alt="company logo"
                  className="size-full h-32 object-contain"
                />
              </a>
            </SwiperSlide>
          );
        })}
      </Swiper>
      <div dir="ltr">
        <Splide
          aria-label="Tesla Hero Slider"
          options={{
            perMove: 1,
            type: "loop",
            arrows: false,
            autoplay: true,
            pagination: false,
            gap: "2em",
            omitEnd: true,
            perPage: 6,
            breakpoints: {
              1580: {
                perPage: 4,
              },
              1024: {
                perPage: 3,
              },
              767: {
                perPage: 2,
              },
            },
          }}
        >
          {companies.map((item, index) => {
            return (
              <SplideSlide key={index} data-splide-interval="2000">
                <Card thing={item} type={type} />
              </SplideSlide>
            );
          })}
        </Splide>
      </div>
    </section>
  );
};

export default Companies;
