import { Link } from "react-router-dom";
import { footLinks, socialButtons } from "../../data/Constants";

const Footer = () => {
  return (
    <footer
      className="w-full bg-slate-900 text-slate-100 text-xs text-center z-20 padding-x"
      dir="rtl"
    >
      <section className="grid md:grid-cols-6 text-start padding gap-8">
        <div className="md:col-span-2">
          <h5 className="title-sm mb-5">
            <i className="fa-solid fa-hashtag" />
            ما کی هستیم؟
          </h5>
        </div>
        {footLinks.map((item, index) => {
          return (
            <div className="">
              {item.describe ? (
                <p></p>
              ) : item.links ? (
                <nav key={index} className="md:col-span-1">
                  <h5 className="title-sm mb-5">
                    <i className="fa-solid fa-hashtag" />
                    {item.title}
                  </h5>
                  <ul>
                    {item.links.map((link, index) => {
                      return (
                        <li>
                          <Link to={link.path}>{link.label}</Link>
                        </li>
                      );
                    })}
                  </ul>
                </nav>
              ) : (
                ""
              )}
            </div>
          );
        })}
      </section>
      <section className="w-full padding-x">
        <hr />
        <div className="flex-seperate">
          <h6>
            <Link to="/about">Code Empire © 2024</Link>
          </h6>
          <ul className="mt-3 pb-3  max-md:mx-auto grid grid-cols-4 gap-4">
            {socialButtons.map((item, index) => {
              return (
                <li key={index}>
                  <a href={item.href}>
                    <i className={item.iconClass} />
                  </a>
                </li>
              );
            })}
          </ul>
        </div>
      </section>
    </footer>
  );
};

export default Footer;
