import { Link } from "react-router-dom";
import { footLinks, socialButtons } from "../../data/Constants";

const Footer = () => {
  return (
    <footer
      className="w-full bg-slate-900 text-slate-100 text-xs text-center z-20 padding-x padding-b"
      dir="rtl"
    >
      <section className="grid md:grid-cols-4 text-start">
        <div>
          <h5 className="title-sm">ما کی هستیم؟</h5>
          <p>
            می‌توان گفت امپراتوری کد بی‌همتاست و هیچ رقیبی ندارد! آکادمی‌ای که
            نه‌تنها برنامه‌نویسی را آموزش می‌دهد، بلکه آینده حرفه‌ای تو را در
            دنیای فناوری و برنامه‌نویسی می‌سازد.
          </p>
        </div>
        {footLinks.map((item, index) => {
          return (
            <nav key={index}>
              <h5 className="title-sm">{item.title}</h5>
              <ul>
                {item.links.map((link, index) => {
                  return (
                    <li>
                      <Link to={link.path}>{link.label}</Link>
                    </li>
                  );
                })}
              </ul>
            </nav>
          );
        })}
      </section>
      <ul className="mt-3 pb-3 absolute left-0 right-0 bottom-0 max-w-32 mx-auto md:w-4/5 grid grid-cols-4 gap-4">
        {socialButtons.map((item, index) => {
          return (
            <li key={index}>
              <a href={item.href}>
                <i className={item.iconClass} />
              </a>
            </li>
          );
        })}
      </ul>
    </footer>
  );
};

export default Footer;
