import { useParams } from "react-router-dom";
import { policy } from "../data/Policy";
import { Article, Hero } from "../comps/Portal";

const Single_Rule = () => {
  const { rule } = useParams();
  const theRule = policy.find(
    (e) =>
      e &&
      e.title.replace(/\//g, "%2F").replace(/\s+/g, "-").toLowerCase() ===
        rule.replace(/\//g, "%2F").replace(/\s+/g, "-").toLowerCase()
  );
  return (
    <>
      <Hero title={theRule.title} />
      <div className="grid grid-cols-5 padding container">
        <main className="lg:col-span-3">
          <Article article={theRule.article} />
        </main>
        <aside>
          <section className="py-3 warn flex-seperate">
            <h5>ظرفیت دوره :</h5>
            <h5>{capacity > 0 ? `${capacity} نفر` : "ظرفیت پر شد"}</h5>
          </section>
        </aside>
      </div>
    </>
  );
};

export default Single_Rule;
