import { Link } from "react-router-dom";
import { Loaded_Animation } from "../Portal";

const Stand = ({ thing, index, custromClass }) => {
  const heights = ["h-20", "h-36", "h-28"];
  const { ref, className } = Loaded_Animation("h-0", heights[index]);
  const path = "/courses/" + thing.path;
  return (
    <div className="w-1/3 flex flex-col justify-end h-36">
      <div
        ref={ref}
        className={`flex-fullcenter transition-all rounded-t-xl ${custromClass}`}
        style={{
          backgroundColor: thing.color[0],
          background: `linear-gradient(0deg, ${thing.color[0]} 0%, ${thing.color[1]} 100%)`,
          overflow: "hidden",
        }}
      >
        <Link to={path} className="text-center text-slate-100">
          <h2 className="title-res">{thing.title}</h2>
          <h2 className="para mix-blend-soft-light">{thing.persianTitle}</h2>
        </Link>
      </div>
    </div>
  );
};

export default Stand;
