import { useState, useEffect, useRef } from "react";
import { about } from "../../data/Constants";

const Numbers = () => {
  const [hasCounted, setHasCounted] = useState(false);
  const numbersRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting && !hasCounted) {
          setHasCounted(true);
        }
      },
      { threshold: 0.5 } // Adjust this value if needed
    );

    if (numbersRef.current) {
      observer.observe(numbersRef.current);
    }

    return () => {
      if (numbersRef.current) {
        observer.unobserve(numbersRef.current);
      }
    };
  }, [hasCounted]);

  const countToNumber = (targetNumber, duration) => {
    const [count, setCount] = useState(0);

    useEffect(() => {
      if (hasCounted) {
        let start = 0;
        const increment = targetNumber / (duration / 10);
        const interval = setInterval(() => {
          start += increment;
          if (start >= targetNumber) {
            setCount(targetNumber);
            clearInterval(interval);
          } else {
            setCount(Math.ceil(start));
          }
        }, 10);

        return () => clearInterval(interval);
      }
    }, [hasCounted, targetNumber, duration]);

    return count;
  };

  return (
    <section className="bg-blue-50 padding" ref={numbersRef}>
      <div className="container grid grid-cols-7 md:gap-x-5 gap-3">
        {about.numbers.map((item, index) => {
          const count = countToNumber(item.number, 2000); // 2000ms for the duration
          return (
            <div className="text-center" key={index}>
              <h6 className="text-blue-500 title-lg">{count} +</h6>
              <h4 className="md:text-sm text-[10px] md:mt-3 mt-1">
                {item.title}
              </h4>
              {index !== about.numbers.length - 1 && (
                <div className="w-[1px] bg-slate-950 h-full mx-auto bg-opacity-10" />
              )}
            </div>
          );
        })}
      </div>
    </section>
  );
};

export default Numbers;
